<footer class="relative bg-[#502C58] text-white px-6 py-8 z-0 overflow-hidden">
    <!-- Decorative background cats image -->
    <img src="/images/footer-cats.svg" alt="Decorative cats"
        class="absolute top-[-50px] right-[-5%] h-[150%] w-auto z-0 pointer-events-none opacity-50 mix-blend-luminosity" />

    <div class="container mx-auto grid grid-cols-1 md:grid-cols-3 gap-12 mb-12 relative z-10">
        <!-- Left column: logos + tagline + map -->
        <div class="flex flex-col space-y-4">
            <!-- Logos -->
            <div class="flex flex-wrap items-center justify-center space-x-2 md:space-x-4">
                <img src="/images/pup-logo.svg" alt="PUP Logo" class="h-16 md:h-20">
                <img src="/images/sintang-pusa-logo.svg" alt="Sintang Pusa Logo" class="h-16 md:h-20">
                <img src="/images/sintang-pusa-title.svg" alt="Sintang Pusa Title" class="h-16 md:h-20">
            </div>

            <!-- Tagline -->
            <p class="inline-block bg-[#E7AB39] font-semibold px-6 py-2 rounded-full w-full">
                For the cats of PUP, we serve
            </p>

            <!-- Map -->
            <div class="w-full rounded-lg overflow-hidden bg-[#3d2243] flex-shrink-0">
                <iframe
                src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d8181.291339801293!2d121.00257945568012!3d14.595926822042836!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3397c9dd976ededf%3A0xbaaa357c5585b8c2!2sPolytechnic%20University%20of%20the%20Philippines!5e0!3m2!1sen!2sph!4v1747059064980!5m2!1sen!2sph"
                class="w-full h-60"
                style="border:0;"
                allowfullscreen
                loading="lazy"
                referrerpolicy="no-referrer-when-downgrade">
                </iframe>
            </div>
        </div>

        <!-- Middle column: Find Us & Operating Hours -->
